import{_ as e,c as a,o as t,a4 as r}from"./chunks/framework.B7YSrJ1r.js";const b=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"Learning/CN/index.md","filePath":"Learning/CN/index.md","lastUpdated":1726028916000}'),o={name:"Learning/CN/index.md"},n=r('<h4 id="computer-network" tabindex="-1"><strong>Computer network</strong> <a class="header-anchor" href="#computer-network" aria-label="Permalink to &quot;**Computer network**&quot;">​</a></h4><ul><li>A collection of interconnected devices that share resources and communicate using standard protocols.</li></ul><h4 id="osi-model" tabindex="-1"><strong>OSI model</strong> <a class="header-anchor" href="#osi-model" aria-label="Permalink to &quot;**OSI model**&quot;">​</a></h4><ul><li>A framework that divides networking into 7 layers: Physical, Data Link, Network, Transport, Session, Presentation, and Application.</li></ul><h4 id="tcp-vs-udp" tabindex="-1"><strong>TCP vs UDP</strong> <a class="header-anchor" href="#tcp-vs-udp" aria-label="Permalink to &quot;**TCP vs UDP**&quot;">​</a></h4><ul><li><strong>TCP</strong> is reliable and connection-oriented. <strong>UDP</strong> is faster, connectionless, and less reliable.</li></ul><h4 id="ip-address" tabindex="-1"><strong>IP Address</strong> <a class="header-anchor" href="#ip-address" aria-label="Permalink to &quot;**IP Address**&quot;">​</a></h4><ul><li>A unique identifier for devices on a network, used for routing data between them.</li></ul><h4 id="ipv4-vs-ipv6" tabindex="-1"><strong>IPv4 vs IPv6</strong> <a class="header-anchor" href="#ipv4-vs-ipv6" aria-label="Permalink to &quot;**IPv4 vs IPv6**&quot;">​</a></h4><ul><li><strong>IPv4</strong>: 32-bit, supports 4.3 billion devices. <strong>IPv6</strong>: 128-bit, supports a vastly larger number of devices.</li></ul><h4 id="mac-address" tabindex="-1"><strong>MAC Address</strong> <a class="header-anchor" href="#mac-address" aria-label="Permalink to &quot;**MAC Address**&quot;">​</a></h4><ul><li>A unique hardware identifier for network devices used at the Data Link Layer.</li></ul><h4 id="dns-domain-name-system" tabindex="-1"><strong>DNS (Domain Name System)</strong> <a class="header-anchor" href="#dns-domain-name-system" aria-label="Permalink to &quot;**DNS (Domain Name System)**&quot;">​</a></h4><ul><li>Translates domain names (e.g., google.com) into IP addresses for communication.</li></ul><h4 id="dhcp-dynamic-host-configuration-protocol" tabindex="-1"><strong>DHCP (Dynamic Host Configuration Protocol)</strong> <a class="header-anchor" href="#dhcp-dynamic-host-configuration-protocol" aria-label="Permalink to &quot;**DHCP (Dynamic Host Configuration Protocol)**&quot;">​</a></h4><ul><li>Automatically assigns IP addresses to devices in a network, simplifying management.</li></ul><h4 id="subnet-mask" tabindex="-1"><strong>Subnet Mask</strong> <a class="header-anchor" href="#subnet-mask" aria-label="Permalink to &quot;**Subnet Mask**&quot;">​</a></h4><ul><li>Defines the network and host portions of an IP address, aiding in efficient IP address allocation.</li></ul><h4 id="router-vs-switch" tabindex="-1"><strong>Router vs Switch</strong> <a class="header-anchor" href="#router-vs-switch" aria-label="Permalink to &quot;**Router vs Switch**&quot;">​</a></h4><ul><li><strong>Router</strong>: Connects multiple networks and routes data between them.</li><li><strong>Switch</strong>: Connects devices within a network, forwarding data based on MAC addresses.</li></ul><h4 id="hub-vs-switch" tabindex="-1"><strong>Hub vs Switch</strong> <a class="header-anchor" href="#hub-vs-switch" aria-label="Permalink to &quot;**Hub vs Switch**&quot;">​</a></h4><ul><li><strong>Hub</strong>: Broadcasts data to all devices.</li><li><strong>Switch</strong>: Directs data to the intended recipient.</li></ul><h4 id="firewall" tabindex="-1"><strong>Firewall</strong> <a class="header-anchor" href="#firewall" aria-label="Permalink to &quot;**Firewall**&quot;">​</a></h4><ul><li>Controls incoming and outgoing network traffic based on security rules, protecting against unauthorized access.</li></ul><h4 id="nat-network-address-translation" tabindex="-1"><strong>NAT (Network Address Translation)</strong> <a class="header-anchor" href="#nat-network-address-translation" aria-label="Permalink to &quot;**NAT (Network Address Translation)**&quot;">​</a></h4><ul><li>Translates private IP addresses to a public one, allowing multiple devices to share a single public IP.</li></ul><h4 id="static-vs-dynamic-ip-addressing" tabindex="-1"><strong>Static vs Dynamic IP Addressing</strong> <a class="header-anchor" href="#static-vs-dynamic-ip-addressing" aria-label="Permalink to &quot;**Static vs Dynamic IP Addressing**&quot;">​</a></h4><ul><li><strong>Static</strong>: Manually assigned and permanent.</li><li><strong>Dynamic</strong>: Automatically assigned via DHCP and can change over time.</li></ul><h4 id="vpn-virtual-private-network" tabindex="-1"><strong>VPN (Virtual Private Network)</strong> <a class="header-anchor" href="#vpn-virtual-private-network" aria-label="Permalink to &quot;**VPN (Virtual Private Network)**&quot;">​</a></h4><ul><li>Creates an encrypted tunnel for secure communication over a public network like the internet.</li></ul><h4 id="three-way-handshake-tcp" tabindex="-1"><strong>Three-Way Handshake (TCP)</strong> <a class="header-anchor" href="#three-way-handshake-tcp" aria-label="Permalink to &quot;**Three-Way Handshake (TCP)**&quot;">​</a></h4><ul><li>Used to establish a TCP connection: SYN, SYN-ACK, and ACK packets are exchanged to confirm readiness.</li></ul><h4 id="proxy-server" tabindex="-1"><strong>Proxy Server</strong> <a class="header-anchor" href="#proxy-server" aria-label="Permalink to &quot;**Proxy Server**&quot;">​</a></h4><ul><li>Acts as an intermediary between client and server, often used for security, caching, and anonymity.</li></ul><h4 id="arp-address-resolution-protocol" tabindex="-1"><strong>ARP (Address Resolution Protocol)</strong> <a class="header-anchor" href="#arp-address-resolution-protocol" aria-label="Permalink to &quot;**ARP (Address Resolution Protocol)**&quot;">​</a></h4><ul><li>Resolves IP addresses to MAC addresses, enabling devices to communicate on a local network.</li></ul><h4 id="latency" tabindex="-1"><strong>Latency</strong> <a class="header-anchor" href="#latency" aria-label="Permalink to &quot;**Latency**&quot;">​</a></h4><ul><li>The delay between sending and receiving data, measured in milliseconds (ms).</li></ul><h4 id="bandwidth" tabindex="-1"><strong>Bandwidth</strong> <a class="header-anchor" href="#bandwidth" aria-label="Permalink to &quot;**Bandwidth**&quot;">​</a></h4><ul><li>The maximum rate of data transfer across a network, measured in bits per second (bps).</li></ul><h4 id="throughput" tabindex="-1"><strong>Throughput</strong> <a class="header-anchor" href="#throughput" aria-label="Permalink to &quot;**Throughput**&quot;">​</a></h4><ul><li>The actual rate of successful data transfer, often lower than bandwidth due to network conditions.</li></ul><h4 id="packet-loss" tabindex="-1"><strong>Packet Loss</strong> <a class="header-anchor" href="#packet-loss" aria-label="Permalink to &quot;**Packet Loss**&quot;">​</a></h4><ul><li>Occurs when one or more packets fail to reach their destination due to network issues.</li></ul><h4 id="vlan-virtual-local-area-network" tabindex="-1"><strong>VLAN (Virtual Local Area Network)</strong> <a class="header-anchor" href="#vlan-virtual-local-area-network" aria-label="Permalink to &quot;**VLAN (Virtual Local Area Network)**&quot;">​</a></h4><ul><li>A logical grouping of devices within a network, improving segmentation and security.</li></ul><h4 id="socket" tabindex="-1"><strong>Socket</strong> <a class="header-anchor" href="#socket" aria-label="Permalink to &quot;**Socket**&quot;">​</a></h4><ul><li>An endpoint for communication, identified by an IP address and port number, facilitating data transfer between devices.</li></ul><h4 id="port-forwarding" tabindex="-1"><strong>Port Forwarding</strong> <a class="header-anchor" href="#port-forwarding" aria-label="Permalink to &quot;**Port Forwarding**&quot;">​</a></h4><ul><li>Maps external traffic to a specific internal IP and port, allowing access to services on a private network.</li></ul><h4 id="load-balancer" tabindex="-1"><strong>Load Balancer</strong> <a class="header-anchor" href="#load-balancer" aria-label="Permalink to &quot;**Load Balancer**&quot;">​</a></h4><ul><li>Distributes traffic across multiple servers to enhance performance and reliability.</li></ul><h4 id="icmp-internet-control-message-protocol" tabindex="-1"><strong>ICMP (Internet Control Message Protocol)</strong> <a class="header-anchor" href="#icmp-internet-control-message-protocol" aria-label="Permalink to &quot;**ICMP (Internet Control Message Protocol)**&quot;">​</a></h4><ul><li>Used for error reporting and diagnostic functions like <code>ping</code> to check connectivity.</li></ul><h4 id="traceroute" tabindex="-1"><strong>Traceroute</strong> <a class="header-anchor" href="#traceroute" aria-label="Permalink to &quot;**Traceroute**&quot;">​</a></h4><ul><li>A tool that shows the path packets take from one device to another, used for troubleshooting network issues.</li></ul><h4 id="ssl-tls" tabindex="-1"><strong>SSL/TLS</strong> <a class="header-anchor" href="#ssl-tls" aria-label="Permalink to &quot;**SSL/TLS**&quot;">​</a></h4><ul><li>Security protocols that encrypt data sent over the internet, ensuring secure communication.</li></ul><h4 id="cdn-content-delivery-network" tabindex="-1"><strong>CDN (Content Delivery Network)</strong> <a class="header-anchor" href="#cdn-content-delivery-network" aria-label="Permalink to &quot;**CDN (Content Delivery Network)**&quot;">​</a></h4><ul><li>A network of distributed servers that deliver web content from locations closer to users for faster access.</li></ul><h4 id="qos-quality-of-service" tabindex="-1"><strong>QoS (Quality of Service)</strong> <a class="header-anchor" href="#qos-quality-of-service" aria-label="Permalink to &quot;**QoS (Quality of Service)**&quot;">​</a></h4><ul><li>Techniques to prioritize certain types of traffic (e.g., VoIP) to ensure high performance for critical applications.</li></ul><h4 id="collision-domain-vs-broadcast-domain" tabindex="-1"><strong>Collision Domain vs Broadcast Domain</strong> <a class="header-anchor" href="#collision-domain-vs-broadcast-domain" aria-label="Permalink to &quot;**Collision Domain vs Broadcast Domain**&quot;">​</a></h4><ul><li><strong>Collision Domain</strong>: A network segment where data collisions can occur.</li><li><strong>Broadcast Domain</strong>: A network segment where broadcast traffic is received by all devices.</li></ul><h4 id="bgp-border-gateway-protocol" tabindex="-1"><strong>BGP (Border Gateway Protocol)</strong> <a class="header-anchor" href="#bgp-border-gateway-protocol" aria-label="Permalink to &quot;**BGP (Border Gateway Protocol)**&quot;">​</a></h4><ul><li>A protocol used to exchange routing information between different networks on the internet.</li></ul><h4 id="dns-server" tabindex="-1"><strong>DNS Server</strong> <a class="header-anchor" href="#dns-server" aria-label="Permalink to &quot;**DNS Server**&quot;">​</a></h4><ul><li>Resolves domain names into IP addresses, enabling access to websites via human-readable names.</li></ul><h4 id="half-duplex-vs-full-duplex" tabindex="-1"><strong>Half-Duplex vs Full-Duplex</strong> <a class="header-anchor" href="#half-duplex-vs-full-duplex" aria-label="Permalink to &quot;**Half-Duplex vs Full-Duplex**&quot;">​</a></h4><ul><li><strong>Half-Duplex</strong>: Data flows in one direction at a time.</li><li><strong>Full-Duplex</strong>: Data flows in both directions simultaneously.</li></ul><h4 id="multicast" tabindex="-1"><strong>Multicast</strong> <a class="header-anchor" href="#multicast" aria-label="Permalink to &quot;**Multicast**&quot;">​</a></h4><ul><li>Sends data to multiple devices in a group, more efficient than sending individual unicast messages.</li></ul><h4 id="internet-vs-intranet" tabindex="-1"><strong>Internet vs Intranet</strong> <a class="header-anchor" href="#internet-vs-intranet" aria-label="Permalink to &quot;**Internet vs Intranet**&quot;">​</a></h4><ul><li><strong>Internet</strong>: Global public network.</li><li><strong>Intranet</strong>: Private network within an organization.</li></ul><h4 id="strongly-connected-components-in-graph" tabindex="-1"><strong>Strongly Connected Components in Graph</strong> <a class="header-anchor" href="#strongly-connected-components-in-graph" aria-label="Permalink to &quot;**Strongly Connected Components in Graph**&quot;">​</a></h4><ul><li>A component of a directed graph where every vertex is reachable from every other vertex in that component.</li></ul><h4 id="bridges-in-graph" tabindex="-1"><strong>Bridges in Graph</strong> <a class="header-anchor" href="#bridges-in-graph" aria-label="Permalink to &quot;**Bridges in Graph**&quot;">​</a></h4><ul><li>A bridge is an edge in a graph, whose removal increases the number of connected components in the graph.</li></ul><h4 id="traceroute-and-ping-difference" tabindex="-1"><strong>Traceroute and Ping Difference</strong> <a class="header-anchor" href="#traceroute-and-ping-difference" aria-label="Permalink to &quot;**Traceroute and Ping Difference**&quot;">​</a></h4><ul><li><strong>Ping</strong>: Measures the round-trip time for packets.</li><li><strong>Traceroute</strong>: Shows the path packets take between devices, useful for troubleshooting.</li></ul>',80),i=[n];function s(l,d,c,u,h,g){return t(),a("div",null,i)}const f=e(o,[["render",s]]);export{b as __pageData,f as default};
